mapper.insert:
INSERT INTO GAME
VALUES(GAME_SEQ.NEXTVAL, #{category}, #{name_team1}, null, #{gdate}, #{name_stadium}, #{title}, #{content}
	, 0, 0, 0, 0, 0, null, null, 0, SYSDATE)

mapper.update:
UPDATE MEMBER
SET NOW_MONEY=#{now_money}, NOW_POINT=#{now_point}
WHERE ID=#{id}

mapper.select:
    	SELECT DISTINCT PRICE, POINT 
    	FROM BOOKING
		WHERE SEQ_GAME=#{seq_game}

mapper.update:
UPDATE MEMBER
SET NOW_MONEY=NOW_MONEY+#{now_money}, NOW_POINT=NOW_POINT+#{now_point}
WHERE ID=#{id}

mapper.select:
SELECT SEQ_BOOK, SEQ_GAME, ID, BDATE, PRICE, NAME_STADIUM, BOOK_TIME
FROM BOOKING
WHERE SEQ_GAME=#{seq_game}

mapper.update:
UPDATE GAME
SET DEL=1
WHERE SEQ_GAME=#{seq_game}

UPDATE GAME
SET READ_COUNT = READ_COUNT + 1
WHERE SEQ_GAME=#{seq_game}

mapper.select:
SELECT READ_COUNT
FROM GAME
WHERE SEQ_GAME=#{seq_game}

mapper.update:
UPDATE VIDEO
SET READ_COUNT = READ_COUNT+1
WHERE SEQ_VIDEO=#{seq_video}

mapper.select:
SELECT * FROM VIDEO_COMMENT
WHERE PARENT=#{parent}
ORDER BY REF DESC, STEP ASC
